// import { mapState, mapMutations } from 'vuex'
export default {
    name:'NotApproved',
    props:["bid"],
    data() {
      return {
        // noList:[],
        multipleSelection: [],
        tableData:[],
        // 数据备份
        list:[],
        total:7,
        display:true,
        dispaly1:true,
        pageIndex:1
      }
    },
    methods:{
      // ...mapMutations('commentaryOptions',{currentCommentary:'CURRENTCOMMENTARY'}),
      toggleSelection(rows) {
        if (rows) {
        rows.forEach(row => {
          this.$refs.multipleTable.toggleRowSelection(row);
        });
        } else {
          this.$refs.multipleTable.clearSelection();
        }
      },
      handleSelectionChange(val) {
        this.multipleSelection = val;
      },
      handelCurrentChange(val){
        this.pageIndex=val
        this.getCommentary()
      },
      // 获取评论
      getCommentary(){
        // this.noList=this.commentaryList
        // this.noList = this.noList.filter((li)=>{
        //   return li.ctype==0
        // })
        let bid=sessionStorage.getItem("abid")
        this.$axios.post("http://localhost:8081/api/commentary/findByIdPage",{
          bid:this.bid||bid,
          pageIndex:this.pageIndex,
          pageSize:7
        }).then(res=>{
          // console.log(res.data);
          this.tableData=res.data.records
          this.total=res.data.total
          this.list=res.data.records
          if(this.display==false){
              this.hide1()
          }
          this.$nextTick(function(){
            if(this.dispaly1==false){
              this.hide2()
            }
          })
        },error=>{
          console.log(error.message);
        })
      },
      // 通过审核
      currentComm(id){
        this.$axios.post('http://localhost:8081/api/commentary/updateType',{
          cid:id,
          ctype:1
        })
        .then(res=>{
          if(res.data){
            // alert('审核通过成功')
            this.$message.success('success')
            // this.currentCommentary(bid)
          }else{
            // alert('审核失败，请重试')
            this.$message.warning('失败，请重试')
          }
        },
        error=>{
          console.log(error.message);
          this.$message.warning('失败，请重试')
        })
      },
      //  选中
      // checked2(li){
      //   if(!li.checked){
      //     if(li.checked==null||li.checked==undefined){
      //       this.$set(li,'checked',true)
      //     }else{
      //       li.checked=true
      //     }
      //   }else{
      //     li.checked=false
      //   }
      // },
      // 批量通过
      adoptCommAll(){
        let comm=[]
        this.multipleSelection.forEach(li=>{
          comm.push(li.cid)
        })
        if(comm==''){
          this.$message.warning("选择为空")
          return false
        }
        this.$axios.post('http://localhost:8081/api/commentary/updateAll',comm)
        .then(res=>{
          if (res.data) {
            this.$message.success("成功")
            // alert('成功')
            this.currentCommentary(sessionStorage.getItem('abid'))
          }else{
            this.$message.warning("失败，请重试")
          }
        },
        error=>{
          console.log(error.message);
          this.$message.warning("失败，请重试")
          // alert('失败,请重试')
        })
      },
      // 批量删除
      delCommAll(){
        let ids=[]
        // this.noList.forEach(li=>{
        //   if(li.checked){
        //     ids.push(li.cid)
        //   }
        // })
        this.multipleSelection.forEach(li=>{
          ids.push(li.cid)
        })
        if(ids==''){
          this.$message.warning('选择为空')
          return false
        }
        this.$axios.post('http://localhost:8081/api/commentary/delAll',ids)
        .then(res=>{
          if(res.data){
            // alert('成功')
            this.$message.success("成功")
              // this.currentCommentary(sessionStorage.getItem('abid'))
          }
        },error=>{
          console.log(error.message);
          this.$message.warning("失败")
          // alert('失败')
        })
      },
      // 删除评论
      delComm(id){
        this.$axios.get(`http://localhost:8081/api/commentary/del/${id}`)
        .then(res=>{
          if(res.data){
            // alert('删除成功')
            this.$message.success("删除成功'")

            // this.currentCommentary(bid)
          }else(
            // alert('删除失败，请重试')
            this.$message.warning("删除失败，请重试")
          )
        },
        error=>{
          console.log(error.message);
          this.$message.warning("删除失败，请重试")
        })
      },
      // 隐藏已通过
      hide1(){
        this.display=false
        this.tableData = this.tableData.filter((li)=>{
          return li.ctype==0
        })
      },
      show1(){
        if(this.dispaly1){
          // this.getCommentary()
          this.tableData=this.list
        }else{
          this.tableData=this.list.filter((li)=>{
            return li.ctype==1
          })
        }
        this.$nextTick(function(){
          this.display=true
        })
      },
      hide2(){
        this.dispaly1=false
        this.tableData=this.tableData.filter((li)=>{
          return li.ctype==1
        })
      },
      show2(){
        if(this.display){
          this.tableData=this.list
        }else{
          this.tableData=this.list.filter((li)=>{
            return li.ctype==0
          })
        }
        this.dispaly1=true
      },
    },
    computed:{
      // ...mapState('commentaryOptions',{commentaryList:'commentaryList'})
    },
    mounted(){
      // 初始化
      let abid=sessionStorage.getItem('abid')
      if(abid==null||abid==''){
        // this.currentCommentary(this.bid)
        sessionStorage.setItem("abid",this.bid)
      }
      this.getCommentary()
      //else{
      //   this.currentCommentary(abid)
      // }
      // setInterval(() => {
        // this.getCommentary()
        // this.setCommentaryList()
      // }, 200)
      // setTimeout(() => {
      //   // this.setCommentaryList()
      // }, 100);
    }
}
